public class Part3 {
    
    
    void testCountGenes(){

        System.out.println("2: "+countGenes(""));
        
    }
    
    
    
    int countGenes(String dna){
         
        return printAllGenes(dna, 0);
        
    }

    
    int findStopCodon(String dna, int startIndex, String stopCodon){
        
        int index = startIndex + 3;
        
        while(true){
            
            index = dna.indexOf(stopCodon, index);
            
            if(index == -1) break;
            
            if( (index - startIndex)%3 == 0 ){
                
                return index;
                
            }
            
            index++;
            
        }
        
        return dna.length();
        
    }
    
    
    String findGene(String dna){
        
        int start = dna.indexOf("ATG");
        
        if(start == -1) return "";
        
        
        int endTAA = findStopCodon(dna, start, "TAA");
        
        int endTAG = findStopCodon(dna, start, "TAG");
        
        int endTGA = findStopCodon(dna, start, "TGA");
        
        
        int min = endTAA;
        
        min = Math.min(min, endTAG);
        
        min = Math.min(min, endTGA);
        
        if(min == dna.length()) return "";
        
        
        return dna.substring(start, min + 3);
        
    }
    
    
    int printAllWithStop(int start, String dna, String end){
        

        
        int index = start;
        
        int count = 0;
        
        while(true){
            index = findStopCodon(dna, index, end);
            
            if(index == dna.length()) break;
            
            
            //System.out.println(dna.substring(start, index+3));
            if(dna.substring(start, index+3).length() > 60){ 
                count++;
            }
                
            index++;
        }
        
        return count;
        
        
    }
    
    int printAllGenes(String dna, int start){
        
        start = dna.indexOf("ATG", start);
        
        if(start == -1) return 0;
        
        int count = 0;
        count += printAllWithStop(start, dna, "TAA");
        count += printAllWithStop(start, dna, "TAG");
        count += printAllWithStop(start, dna, "TGA");
        
        count+= printAllGenes(dna, start+1);

        return count;
    }

}
